INSERT INTO public.symptoms (id, name, category, severities) VALUES
('cough', 'سعال', 'Respiratory', '["mild", "moderate", "severe"]'),
('shortness_of_breath', 'ضيق في التنفس', 'Respiratory', '["mild", "moderate", "severe"]'),
('sore_throat', 'التهاب الحلق', 'Respiratory', '["mild", "moderate", "severe"]'),
('runny_nose', 'سيلان الأنف', 'Respiratory', '["mild", "moderate"]'),
('chest_pain', 'ألم في الصدر', 'Cardiovascular', '["mild", "moderate", "severe"]'),
('palpitations', 'خفقان القلب', 'Cardiovascular', '["mild", "moderate", "severe"]'),
('nausea', 'غثيان', 'Digestive', '["mild", "moderate", "severe"]'),
('vomiting', 'قيء', 'Digestive', '["mild", "moderate", "severe"]'),
('abdominal_pain', 'ألم في البطن', 'Digestive', '["mild", "moderate", "severe"]'),
('diarrhea', 'إسهال', 'Digestive', '["mild", "moderate", "severe"]'),
