INSERT INTO public.treatments (id, name, description, type, dosage, instructions, duration, side_effects, precautions) VALUES
('rest', 'الراحة', 'احصل على قسط كافٍ من النوم والراحة لمساعدة جسمك على التعافي وتوجيه الطاقة لمحاربة العدوى.', 'نمط حياة', '8 ساعات يومياً', 'حاول النوم في غرفة هادئة ومظلمة. تجنب الإجهاد البدني والذهني.', 'حتى زوال الأعراض تماماً', '["الخمول المؤقت"]', '["تجنب النوم المفرط الذي قد يؤدي إلى الصداع."]'),
('fluids', 'شرب السوائل', 'اشرب الكثير من السوائل مثل الماء والمرق والعصائر الطبيعية للحفاظ على رطوبة الجسم.', 'نمط حياة', '2-3 لتر يومياً', 'اشرب كميات صغيرة بشكل متكرر طوال اليوم. تجنب المشروبات التي تحتوي على الكافيين.', 'طوال فترة المرض', '["كثرة التبول"]', '["تجنب الإفراط في شرب الماء إذا كنت تعاني من مشاكل في الكلى."]'),
('pain_relievers', 'مسكنات الألم', 'مسكنات الألم التي لا تستلزم وصفة طبية مثل الباراسيتامول (الأسيتامينوفين) أو الإيبوبروفين.', 'دواء', 'عند الحاجة (كل 4-6 ساعات)', 'تناول الدواء مع كوب كامل من الماء. لا تتجاوز الجرعة اليومية القصوى.', 'لا تزيد عن 3-5 أيام دون استشارة طبيب', '["غثيان", "ألم في المعدة", "دوخة"]', '["لا تتناول الإيبوبروفين على معدة فارغة.", "تجنب الباراسيتامول إذا كنت تعاني من مشاكل في الكبد."]'),
('antivirals', 'مضادات الفيروسات', 'أدوية بوصفة طبية (مثل أوسيلتاميفير) تعمل على وقف تكاثر الفيروس في الجسم.', 'دواء', 'حسب وصفة الطبيب', 'يجب البدء بها خلال 48 ساعة من ظهور الأعراض للحصول على أفضل النتائج.', 'عادة 5 أيام', '["غثيان", "قيء", "صداع"]', '["أكمل الدورة العلاجية كاملة حتى لو شعرت بتحسن.", "أخبر طبيبك عن أي أدوية أخرى تتناولها."]'),
('monitor_oxygen', 'مراقبة الأكسجين', 'استخدم مقياس التأكسج النبضي (Pulse Oximeter) للتحقق من نسبة تشبع الأكسجين في الدم.', 'إجراء طبي', 'مرتين يومياً', 'ضع الجهاز على إصبع السبابة وانتظر حتى تستقر القراءة. تأكد من أن يدك دافئة.', 'طوال فترة العزل المنزلي', '[]', '["إذا كانت النسبة أقل من 92%، توجه للمستشفى فوراً.", "طلاء الأظافر قد يؤثر على دقة القراءة."]'),
('antacids', 'مضادات الحموضة', 'أدوية تعمل على تحييد حمض المعدة لتخفيف الحرقة وعسر الهضم.', 'دواء', 'بعد الأكل', 'تناولها بعد الوجبات بساعة أو عند الشعور بالأعراض. امضغ الأقراص جيداً قبل البلع.', 'عند الحاجة، لا تستخدم لأكثر من أسبوعين', '["إمساك أو إسهال", "تغير في لون البراز"]', '["قد تتداخل مع امتصاص أدوية أخرى.", "استشر الطبيب إذا كنت تعاني من مشاكل في الكلى."]'),
('dietary_changes', 'تغييرات غذائية', 'تعديل النظام الغذائي لتجنب مهيجات المعدة وتعزيز الشفاء.', 'نمط حياة', 'مستمر', 'تجنب الأطعمة الحارة، الحمضية، المقلية، والدهنية. تناول وجبات صغيرة ومتكررة.', 'نمط حياة مستمر', '[]', '["تأكد من الحصول على تغذية متوازنة رغم القيود."]')
INSERT INTO public.diseases (id, name, description, symptoms, treatments, prevention, causes, complications, diagnosis_method) VALUES
('common_cold', 'نزلات البرد', 'عدوى فيروسية شائعة تصيب الأنف والحلق، وتعتبر من أكثر الأمراض انتشاراً في العالم.', '["cough", "runny_nose", "sore_throat", "fever"]', '["rest", "fluids", "pain_relievers"]', '["غسل اليدين بانتظام بالماء والصابون.", "تجنب الاتصال الوثيق مع الأشخاص المصابين.", "عدم لمس الوجه (الأنف، العينين، الفم) بأيدٍ غير مغسولة.", "تعزيز المناعة من خلال الغذاء الصحي والنوم الكافي."]', '["الفيروسات الأنفية (Rhinoviruses) هي السبب الأكثر شيوعاً.", "فيروسات كورونا الموسمية.", "الفيروس المخلوي التنفسي (RSV)."]', '["التهاب الأذن الوسطى (خاصة عند الأطفال).", "التهاب الجيوب الأنفية.", "تفاقم الربو.", "الالتهاب الرئوي (نادر في الأشخاص الأصحاء)."]', 'يتم التشخيص عادةً بناءً على الأعراض والفحص السريري. نادراً ما تكون هناك حاجة لفحوصات مخبرية إلا لاستبعاد أمراض أخرى.'),
('flu', 'الإنفلونزا', 'عدوى فيروسية تهاجم الجهاز التنفسي (الأنف، الحلق، والرئتين)، وتتميز بظهور مفاجئ للأعراض.', '["fever", "cough", "sore_throat", "runny_nose", "fatigue", "headache"]', '["rest", "fluids", "antivirals", "pain_relievers"]', '["الحصول على لقاح الإنفلونزا السنوي.", "غسل اليدين جيداً وبشكل متكرر.", "تغطية الفم والأنف عند السعال أو العطس.", "تجنب الأماكن المزدحمة خلال موسم الإنفلونزا."]', '["فيروسات الإنفلونزا من النوع A و B.", "تنتقل العدوى عن طريق الرذاذ المتطاير عند السعال أو العطس.", "ملامسة الأسطح الملوثة بالفيروس."]', '["الالتهاب الرئوي البكتيري.", "التهاب الشعب الهوائية.", "التهابات الأذن.", "مشاكل في القلب (نادراً)."]', 'يمكن استخدام اختبارات الإنفلونزا السريعة (RIDTs) للكشف عن الفيروس في غضون 15 دقيقة، أو اختبارات جزيئية أكثر دقة.'),
('covid_19', 'كوفيد-19', 'مرض معدٍ يسببه فيروس كورونا (سارس-كوف-2)، تتراوح أعراضه من خفيفة إلى شديدة الخطورة.', '["fever", "cough", "shortness_of_breath", "fatigue", "loss_of_taste_or_smell"]', '["rest", "fluids", "pain_relievers", "monitor_oxygen"]', '["تلقي اللقاحات والجرعات المعززة.", "ارتداء الكمامة في الأماكن المزدحمة أو المغلقة.", "التباعد الجسدي عن الآخرين.", "تحسين التهوية في الأماكن المغلقة."]', '["فيروس سارس-كوف-2 (SARS-CoV-2).", "ينتشر بشكل رئيسي عبر الرذاذ التنفسي والهباء الجوي.", "الاتصال المباشر مع المصابين."]', '["متلازمة الضائقة التنفسية الحادة (ARDS).", "تلف دائم في الرئة.", "مشاكل في القلب والأوعية الدموية.", "متلازمة ما بعد كوفيد (كوفيد طويل الأمد)."]', 'اختبار تفاعل البوليميراز المتسلسل (PCR) هو المعيار الذهبي. تتوفر أيضاً اختبارات المستضدات السريعة (Rapid Antigen Tests).'),
('gastritis', 'التهاب المعدة', 'التهاب أو تآكل في بطانة المعدة، قد يحدث فجأة (حاد) أو بمرور الوقت (مزمن).', '["nausea", "vomiting", "abdominal_pain"]', '["antacids", "dietary_changes"]', '["تجنب الأطعمة التي تهيج المعدة (الحارة، الحمضية، المقلية).", "التقليل من استخدام مسكنات الألم (NSAIDs).", "التحكم في التوتر.", "تناول وجبات صغيرة ومتكررة."]', '["العدوى بجرثومة المعدة (H. pylori).", "الاستخدام المفرط لمسكنات الألم.", "التوتر الشديد.", "الإفراط في شرب الكحول."]', '["قرحة المعدة.", "نزيف المعدة.", "فقر الدم (الأنيميا).", "زيادة خطر الإصابة بسرطان المعدة (في حالات نادرة)."]', 'التنظير العلوي (Endoscopy) لفحص المعدة وأخذ خزعة، بالإضافة إلى اختبارات الدم والبراز للكشف عن جرثومة المعدة.')
